import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api/v1';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

export const eventAPI = {
  getEvents: (params = {}) => api.get('/events', { params }),
  getEvent: (eventId) => api.get(`/events/${eventId}`),
  getDecisions: () => api.get('/decisions'),
  getTravelTimeline: () => api.get('/timeline/travel'),
  getExerciseRefreshes: () => api.get('/timeline/exercise-refreshes'),
  getDiagnostics: () => api.get('/timeline/diagnostics'),
  getMemberProgress: () => api.get('/member/progress'),
  getMemberProfile: () => api.get('/member/profile'),
  getBiomarkerTrends: () => api.get('/analytics/biomarkers'),
  getTeamActivity: () => api.get('/analytics/team-activity'),
  searchEvents: (query) => api.get('/events', { params: { search: query } }),
};

export default api;
