import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Timeline, Tabs, Spin, Alert, Button, Modal } from 'antd';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from 'recharts';
import { eventAPI } from '../services/api';
import EventCard from './EventCard';
import DecisionModal from './DecisionModal';
import moment from 'moment';

const { TabPane } = Tabs;

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [conversations, setConversations] = useState([]);
  const [decisions, setDecisions] = useState([]);
  const [metrics, setMetrics] = useState({});
  const [journey, setJourney] = useState([]);
  const [selectedDecision, setSelectedDecision] = useState(null);
  const [selectedConv, setSelectedConv] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [convRes, decRes, journeyRes, metricsRes] = await Promise.all([
        eventAPI.getConversations(),
        eventAPI.getDecisions(),
        eventAPI.getJourney(),
        eventAPI.getMetrics(),
      ]);

      setConversations(convRes.data);
      setDecisions(decRes.data);
      setJourney(journeyRes.data);
      setMetrics(metricsRes.data);
    } catch (err) {
      setError('Failed to load dashboard data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDecisionClick = (decision) => setSelectedDecision(decision);
  const handleConvClick = (conv) => setSelectedConv(conv);

  const formatChartData = (data) =>
    (data || []).map((item) => ({
      ...item,
      date: moment(item.timestamp).format('MMM DD'),
    }));

  if (loading)
    return (
      <Spin size="large" style={{ display: 'flex', justifyContent: 'center', marginTop: '100px' }} />
    );
  if (error) return <Alert message={error} type="error" />;

  return (
    <div style={{ padding: '24px' }}>
      {/* Metrics */}
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#1890ff', margin: 0 }}>{metrics.total_events}</h2>
              <p>Total Events</p>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#52c41a', margin: 0 }}>{metrics.total_decisions}</h2>
              <p>Decisions Made</p>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#fa8c16', margin: 0 }}>{metrics.hrv_issues}</h2>
              <p>HRV Issues</p>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#722ed1', margin: 0 }}>{metrics.travel_periods}</h2>
              <p>Travel Periods</p>
            </div>
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row gutter={[16, 16]} style={{ marginTop: '24px' }}>
        <Col span={12}>
          <Card title="Sleep Trends">
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={formatChartData(metrics.sleep_data || [])}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Line type="monotone" dataKey="value" stroke="#8884d8" />
              </LineChart>
            </ResponsiveContainer>
          </Card>
        </Col>
        <Col span={12}>
          <Card title="Adherence Trends">
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={formatChartData(metrics.adherence_data || [])}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="value" fill="#82ca9d" />
              </BarChart>
            </ResponsiveContainer>
          </Card>
        </Col>
      </Row>

      {/* Timeline / Decisions / Journey */}
      <Card style={{ marginTop: '24px' }}>
        <Tabs defaultActiveKey="timeline">
          <TabPane tab="Timeline" key="timeline">
            <Timeline mode="left">
              {conversations.slice(0, 20).map((conv) => (
                <Timeline.Item
                  key={conv.timestamp}
                  color={conv.action_decision ? 'green' : 'blue'}
                  label={moment(conv.timestamp).format('MMM DD, HH:mm')}
                >
                  <EventCard
                    conv={conv}
                    onClick={() => handleConvClick(conv)}
                    showDecisionIndicator={conv.action_decision}
                  />
                </Timeline.Item>
              ))}
            </Timeline>
          </TabPane>

          <TabPane tab="Decisions" key="decisions">
            <Row gutter={[16, 16]}>
              {decisions.map((decision, idx) => (
                <Col span={24} key={idx}>
                  <Card
                    size="small"
                    style={{ cursor: 'pointer' }}
                    onClick={() => handleDecisionClick(decision)}
                  >
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <div>
                        <strong>{decision.decision}</strong>
                        <p style={{ margin: '4px 0 0 0', color: '#666' }}>{decision.reason}</p>
                      </div>
                      <Button type="link">View Context</Button>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </TabPane>

          <TabPane tab="Journey" key="journey">
            <Timeline>
              {journey.map((ep, idx) => (
                <Timeline.Item key={idx}>
                  <Card size="small">
                    <h4>{ep.episode}</h4>
                    <p>
                      {moment(ep.start_date).format('MMM DD')} -{' '}
                      {moment(ep.end_date).format('MMM DD, YYYY')}
                    </p>
                    <p>{ep.events.length} events during this episode</p>
                  </Card>
                </Timeline.Item>
              ))}
            </Timeline>
          </TabPane>
        </Tabs>
      </Card>

      <DecisionModal
        decision={selectedDecision}
        visible={!!selectedDecision}
        onClose={() => setSelectedDecision(null)}
      />

      <Modal
        title="Conversation Details"
        open={!!selectedConv}
        onCancel={() => setSelectedConv(null)}
        footer={null}
        width={800}
      >
        {selectedConv && (
          <div>
            <p>
              <strong>Actor:</strong> {selectedConv.sender} ({selectedConv.role})
            </p>
            <p>
              <strong>Topic:</strong> {selectedConv.topic}
            </p>
            <p>
              <strong>Message:</strong> {selectedConv.message}
            </p>
            <p>
              <strong>Episode:</strong> {selectedConv.episode} (Week {selectedConv.week}, Month {selectedConv.month})
            </p>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default Dashboard;
