import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Timeline, Tabs, Spin, Alert, Button, Modal } from 'antd';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from 'recharts';
import { eventAPI } from '../services/api';
import EventCard from './EventCard';
import DecisionModal from './DecisionModal';
import moment from 'moment';

const { TabPane } = Tabs;

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [conversations, setConversations] = useState([]);
  const [decisions, setDecisions] = useState([]);
  const [metrics, setMetrics] = useState({});
  const [journey, setJourney] = useState([]);
  const [selectedDecision, setSelectedDecision] = useState(null);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [
        convRes,
        decRes,
        journeyRes,
        metricsRes
      ] = await Promise.all([
        eventAPI.getConversations(),
        eventAPI.getDecisions(),
        eventAPI.getJourney(),
        eventAPI.getMetrics()
      ]);

      setConversations(convRes.data);
      setDecisions(decRes.data);
      setJourney(journeyRes.data);
      setMetrics(metricsRes.data);
    } catch (err) {
      setError('Failed to load dashboard data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDecisionClick = (decision) => setSelectedDecision(decision);
  const handleEventClick = (event) => setSelectedEvent(event);

  const formatChartData = (data) => {
    return (data || []).map(item => ({
      ...item,
      date: moment(item.timestamp).format('MMM DD')
    }));
  };

  if (loading) return <Spin size="large" style={{ display: 'flex', justifyContent: 'center', marginTop: '100px' }} />;
  if (error) return <Alert message={error} type="error" />;

  return (
    <div style={{ padding: '24px' }}>
      <Row gutter={[16, 16]}>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#1890ff', margin: 0 }}>{metrics.total_events}</h2>
              <p>Total Events</p>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#52c41a', margin: 0 }}>{metrics.total_decisions}</h2>
              <p>Decisions Made</p>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#fa8c16', margin: 0 }}>{metrics.hrv_issues}</h2>
              <p>HRV Issues</p>
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#722ed1', margin: 0 }}>{metrics.travel_periods}</h2>
              <p>Travel Periods</p>
            </div>
          </Card>
        </Col>
      </Row>

      <Row gutter={[16, 16]} style={{ marginTop: '24px' }}>
        <Col span={12}>
          <Card title="Sleep Trends">
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={formatChartData(metrics.sleep_data || [])}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Line type="monotone" dataKey="value" stroke="#8884d8" />
              </LineChart>
            </ResponsiveContainer>
          </Card>
        </Col>
        <Col span={12}>
          <Card title="Adherence Trends">
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={formatChartData(metrics.adherence_data || [])}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="value" fill="#82ca9d" />
              </BarChart>
            </ResponsiveContainer>
          </Card>
        </Col>
      </Row>

      <Card style={{ marginTop: '24px' }}>
        <Tabs defaultActiveKey="timeline">
          <TabPane tab="Timeline" key="timeline">
            <Timeline mode="left">
              {conversations.slice(0, 20).map((event) => (
                <Timeline.Item
                  key={event.id}
                  color={event.decision.taken ? 'green' : 'blue'}
                  label={moment(event.timestamp).format('MMM DD, HH:mm')}
                >
                  <EventCard 
                    event={event} 
                    onClick={() => handleEventClick(event)}
                    showDecisionIndicator={event.decision.taken}
                  />
                </Timeline.Item>
              ))}
            </Timeline>
          </TabPane>
          
          <TabPane tab="Decisions" key="decisions">
            <Row gutter={[16, 16]}>
              {decisions.map((decision) => (
                <Col span={24} key={decision.event_id}>
                  <Card 
                    size="small" 
                    style={{ cursor: 'pointer' }}
                    onClick={() => handleDecisionClick(decision)}
                  >
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <div>
                        <strong>{decision.actor.sender}</strong> - {moment(decision.timestamp).format('MMM DD, YYYY HH:mm')}
                        <p style={{ margin: '4px 0 0 0', color: '#666' }}>{decision.rationale}</p>
                      </div>
                      <Button type="link">View Context</Button>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </TabPane>

          <TabPane tab="Travel" key="travel">
            <Timeline>
              {journey.map((period, index) => (
                <Timeline.Item key={index}>
                  <Card size="small">
                    <h4>{period.region} - {period.location}</h4>
                    <p>
                      {moment(period.start_date).format('MMM DD')} - {moment(period.end_date).format('MMM DD, YYYY')}
                    </p>
                    <p>{period.events.length} events during this period</p>
                  </Card>
                </Timeline.Item>
              ))}
            </Timeline>
          </TabPane>
        </Tabs>
      </Card>

      <DecisionModal
        decision={selectedDecision}
        visible={!!selectedDecision}
        onClose={() => setSelectedDecision(null)}
      />

      <Modal
        title="Event Details"
        visible={!!selectedEvent}
        onCancel={() => setSelectedEvent(null)}
        footer={null}
        width={800}
      >
        {selectedEvent && (
          <div>
            <p><strong>Actor:</strong> {selectedEvent.actor.sender} ({selectedEvent.actor.role})</p>
            <p><strong>Category:</strong> {selectedEvent.topic.category} - {selectedEvent.topic.subtype}</p>
            <p><strong>Details:</strong> {selectedEvent.content.details}</p>
            {selectedEvent.why.rationale && (
              <p><strong>Rationale:</strong> {selectedEvent.why.rationale}</p>
            )}
          </div>
        )}
      </Modal>
    </div>
  );
};

export default Dashboard;
