import React from 'react';
import { Card, Tag, Badge } from 'antd';
import { CheckCircleOutlined } from '@ant-design/icons';

const EventCard = ({ event, onClick, showDecisionIndicator }) => {
  const getTopicColor = (category) => {
    const colors = {
      'exercise': 'blue',
      'nutrition': 'green',
      'sleep_recovery': 'purple',
      'labs': 'orange',
      'admin': 'default',
      'travel': 'cyan',
      'cognition': 'magenta'
    };
    return colors[category] || 'default';
  };

  const getRoleColor = (role) => {
    const colors = {
      'Member': 'blue',
      'Concierge': 'green',
      'Medical Strategist': 'red',
      'Physiotherapist': 'orange',
      'Nutritionist': 'purple',
      'Performance Scientist': 'cyan'
    };
    return colors[role] || 'default';
  };

  return (
    <Card 
      size="small" 
      style={{ cursor: 'pointer', position: 'relative' }}
      onClick={onClick}
      hoverable
    >
      {showDecisionIndicator && (
        <Badge 
          count={<CheckCircleOutlined style={{ color: '#52c41a' }} />} 
          style={{ position: 'absolute', top: 8, right: 8 }}
        />
      )}
      
      <div style={{ marginBottom: '8px' }}>
        <Tag color={getRoleColor(event.actor.role)}>{event.actor.sender}</Tag>
        <Tag color={getTopicColor(event.topic.category)}>{event.topic.category}</Tag>
        {event.travel.flag && <Tag color="volcano">Travel</Tag>}
      </div>
      
      <p style={{ margin: 0, fontSize: '12px' }}>
        {event.content.summary.slice(0, 2).join(' ')}
        {event.content.summary.length > 2 && '...'}
      </p>
      
      {event.topic.biomarkers.length > 0 && (
        <div style={{ marginTop: '8px' }}>
          {event.topic.biomarkers.map(biomarker => (
            <Tag key={biomarker} size="small">{biomarker}</Tag>
          ))}
        </div>
      )}
    </Card>
  );
};

export default EventCard;
