import React, { useMemo, useState } from "react";

export default function ConversationLog({ conversations }) {
  const [q, setQ] = useState("");
  const list = Array.isArray(conversations) ? conversations : [];

  const filtered = useMemo(() => {
    if (!q) return list.slice(0, 50);
    const qq = q.toLowerCase();
    return list.filter((c) =>
      [c?.sender, c?.role, c?.topic, c?.message].some((x) => (x || "").toLowerCase().includes(qq))
    ).slice(0, 50);
  }, [list, q]);

  return (
    <div style={card}>
      <h3 style={title}>Conversation Log</h3>
      <input
        value={q}
        onChange={(e) => setQ(e.target.value)}
        placeholder="Search sender, role, topic, message..."
        style={input}
      />
      {!filtered.length ? (
        <div style={{ color: "#64748b" }}>No conversations.</div>
      ) : (
        <div style={{ display: "grid", gap: 8, marginTop: 8, maxHeight: 360, overflow: "auto" }}>
          {filtered.map((c, i) => (
            <div key={i} style={item}>
              <div style={top}>
                <span style={pill}>{c?.role || c?.sender || "—"}</span>
                <span style={{ fontSize: 12, color: "#64748b" }}>{c?.timestamp || "—"}</span>
              </div>
              <div style={{ fontWeight: 600, marginBottom: 4 }}>{c?.topic || "—"}</div>
              <div>{c?.message || "—"}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const card = { background: "#fff", border: "1px solid #e2e8f0", borderRadius: 12, padding: 16 };
const title = { margin: 0, paddingBottom: 8, borderBottom: "1px solid #e2e8f0", fontSize: 16 };
const input = {
  width: "100%",
  border: "1px solid #cbd5e1",
  borderRadius: 8,
  padding: "10px 12px",
  outline: "none",
  marginTop: 12,
};
const item = { background: "#f8fafc", border: "1px solid #e2e8f0", borderRadius: 8, padding: 10 };
const top = { display: "flex", justifyContent: "space-between", marginBottom: 6 };
const pill = { padding: "2px 8px", background: "#e2e8f0", borderRadius: 999, fontSize: 12 };
