// src/components/MemberProfileCard.jsx
import React, { useEffect, useState } from 'react';
import { eventAPI } from '../api';
import LoadingState from './LoadingState';

export default function MemberProfileCard() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError]     = useState('');

  useEffect(() => {
    let mounted = true;
    eventAPI.getMemberProfile()
      .then(res => {
        const data = res.data || {};
        if (mounted) setProfile(data);
      })
      .catch(err => {
        console.error(err);
        if (mounted) setError('Failed to load member profile');
      })
      .finally(() => mounted && setLoading(false));
    return () => { mounted = false; };
  }, []);

  if (loading) return <LoadingState label="Loading member profile..." />;
  if (error)   return <div style={{ color: 'crimson' }}>{error}</div>;
  if (!profile) return <div>No profile data.</div>;

  const name    = profile.member || profile.name || 'Member';
  const age     = profile.age || profile.demographics?.age || '—';
  const loc     = profile.location || profile.demographics?.location || '—';
  const tags    = Array.isArray(profile.tags) ? profile.tags : [];
  const meds    = Array.isArray(profile.medications) ? profile.medications : [];
  const goals   = Array.isArray(profile.goals) ? profile.goals : [];

  return (
    <div style={{
      background: 'white',
      border: '1px solid #eee',
      borderRadius: 12,
      padding: 16,
      display: 'grid',
      gap: 12
    }}>
      <div>
        <h2 style={{ margin: 0 }}>{name}</h2>
        <div style={{ color: '#666', fontSize: 14 }}>
          Age: {age} • Location: {loc}
        </div>
      </div>

      {tags.length > 0 && (
        <Row title="Tags">
          <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
            {tags.map((t, i) => (
              <Pill key={i}>{String(t)}</Pill>
            ))}
          </div>
        </Row>
      )}

      {goals.length > 0 && (
        <Row title="Goals">
          <ul style={{ margin: 0, paddingLeft: 16 }}>
            {goals.map((g, i) => <li key={i}>{String(g)}</li>)}
          </ul>
        </Row>
      )}

      {meds.length > 0 && (
        <Row title="Medications">
          <ul style={{ margin: 0, paddingLeft: 16 }}>
            {meds.map((m, i) => (
              <li key={i}>
                {typeof m === 'string' ? m : (m?.name || 'Medication')}
                {m?.dose ? ` — ${m.dose}` : ''}
              </li>
            ))}
          </ul>
        </Row>
      )}
    </div>
  );
}

function Row({ title, children }) {
  return (
    <div>
      <div style={{ fontSize: 12, color: '#666', marginBottom: 6 }}>{title}</div>
      {children}
    </div>
  );
}

function Pill({ children }) {
  return (
    <span style={{
      fontSize: 12,
      padding: '4px 10px',
      borderRadius: 999,
      background: '#f3f4f6',
      color: '#111827',
      border: '1px solid #e5e7eb'
    }}>
      {children}
    </span>
  );
}
